{
  "title": "Formats by type",
  "type": "pie",
  "params": {
    "shareYAxis": true,
    "addTooltip": true,
    "addLegend": true,
    "legendPosition": "bottom",
    "isDonut": false
  },
  "aggs": [
    {
      "id": "1",
      "enabled": true,
      "type": "count",
      "schema": "metric",
      "params": {}
    },
    {
      "id": "2",
      "enabled": true,
      "type": "filters",
      "schema": "segment",
      "params": {
        "filters": [
          {
            "input": {
              "query": {
                "query_string": {
                  "query": "files.format:(json OR rdf OR xml OR csv OR yaml OR yml)",
                  "analyze_wildcard": true
                }
              }
            },
            "label": "Structured"
          },
          {
            "input": {
              "query": {
                "query_string": {
                  "query": "files.format:(xls OR xlsx OR ods)",
                  "analyze_wildcard": true
                }
              }
            },
            "label": "Semi-structured"
          },
          {
            "input": {
              "query": {
                "query_string": {
                  "query": "files.format:(zip OR 7z OR gzip OR bzip2)",
                  "analyze_wildcard": true
                }
              }
            },
            "label": "Archives"
          },
          {
            "input": {
              "query": {
                "query_string": {
                  "query": "files.format:(txt OR rtf OR odt OR doc OR docx OR pdf)",
                  "analyze_wildcard": true
                }
              }
            },
            "label": "Text"
          },
          {
            "input": {
              "query": {
                "query_string": {
                  "query": "files.format:(gif OR tiff OR jpg OR jpeg OR png)",
                  "analyze_wildcard": true
                }
              }
            },
            "label": "Graphic"
          },
          {
            "input": {
              "query": {
                "query_string": {
                  "query": "files.format:(mpeg OR mkv OR avi OR flv OR mks OR mk3d)",
                  "analyze_wildcard": true
                }
              }
            },
            "label": "Video"
          },
          {
            "input": {
              "query": {
                "query_string": {
                  "query": "files.format:(mp3 OR wav OR mka)",
                  "analyze_wildcard": true
                }
              }
            },
            "label": "Audio"
          },
          {
            "input": {
              "query": {
                "query_string": {
                  "query": "files.format:(swf OR flw)",
                  "analyze_wildcard": true
                }
              }
            },
            "label": "Flash"
          }
        ]
      }
    }
  ],
  "listeners": {}
}

